	// reset general controls for caching
	function royalResetGenerals( colors ) {

		$('[id$=_gen]').each(function() {

			if ( $(this).css('display') === 'none' ) {
				if ( colors === true ) {
					$(this).find('input[id="wp-color-picker"]').val('');
				} else {
					$(this).find('input:text').val('');
					$(this).find('select').val('select');
				}
			}

		});

	}


	// checkbox
	function rcb( id, value ) {
		var selector = $( '#customize-control-royal_' + id ).find('input[type=checkbox]');

		if ( ( selector.is(':checked') && value === true ) || ( !selector.is(':checked') && value === false ) ) {
			return;
		}
		
		selector.prop( 'checked', value ).trigger('change');
	}


	// slider
	function rsd( id, ext, value ) {

		// single slider
		var selector = $( '#customize-control-royal_' + id );

		if ( parseFloat( selector.find('.rf-slider-wrap input').val(), 10 ) !== parseFloat( value, 10 ) ) {

			selector.find('.rf-slider-wrap input').val( value ).trigger('keyup');
			selector.find('.rf-slider-wrap strong').text( value + ext );
			selector.find('.rf-slider-wrap .rf-slider').slider({ value: value });

		}

	}


	// select
	function rst( id, value ) {
		
		var selector = $( '#customize-control-royal_' + id ).find('select');

		if ( selector.val() !== value ) {
			selector.val( value ).trigger('change');
		}

	}


	// radiobutton
	function rrb( id, value ) {
		if ( ! $( '#customize-control-royal_'+ id +' input:radio[value="'+ value +'"]' ).is(':checked') ) {
			$( '#customize-control-royal_'+ id +' input:radio[value="'+ value +'"]' ).prop( 'checked', true ).trigger('change');
		}
	}


	// colorpicker
	function rcp( id, value ) {

		// single color
		var selector = $( '#customize-control-royal_' + id ).find('.wp-color-picker');

		if ( selector.val() !== value ) {
			selector.val( value ).trigger('keyup');
		}

	}

	// text input
	function rti( id, value, uncommon ) {
		var selector = $( '#customize-control-royal_' + id ).find('input');

		if ( uncommon === true ) {
			selector = $( '#customize-control-' + id ).find('input');
		}

		if ( selector.val() !== value ) {
			selector.val( value ).trigger('keyup');
		}
		
	}


	// custom css
	var comment  = '\n\n\n\n/* Theme-Specific CSS */\n\n';

	function royalCustomCSS( css ) {

		if ( css === '' ) {
			comment = '';
		}

		// update Custom CSS field
		var selector = $('#customize-control-royal_custom_css-textarea').find('textarea');
		selector.val( comment + css + '___' ).trigger('keyup');

		var newValue = selector.val();
		selector.val(newValue.replace( '___', '' )).trigger('keyup');

	}


